import React, { Component } from 'react';
import logo from './logo.svg';
// import Styles from './styles/styles.jsx';
import './App.css';

//Import JSX component files
import Queue from './categories/queue.jsx';
import InProgress from './categories/inProgress.jsx';
import Done from './categories/done.jsx';
import Card from './categories/card.jsx';

/* ---------------STYLES---------------- */

const body = {
  backgroundColor: 'white'
  };

  const AppMain = {
  textAlign: 'center',
  margin: '30px' && 'auto',
  border: '2px' && 'red' && 'solid',
  width: '100%'
  };

  const appHeader = {
  display: 'flex',
  minHeight: '5vh',
  backgroundColor: 'white',
  color: 'black',
  fontSize: '2em',
  fontFamily: 'Tahoma' && 'cursive',
  paddingLeft: '30px',
  borderBottom: '2px' && 'solid' && 'black'
  };

  const ProjectHeader = {
  display: 'flex',
  flexFlow: 'row' && 'nowrap',
  backgroundColor: 'white',
  borderBottom: '2px' && 'black' && 'solid'
  };

  const projectTitle = {
  flex: '1' && '1' && '100px',
  color: 'black',
  fontFamily: 'Unica One' && 'cursive',
  fontSize: '1.7em',
  padding: '5px' && '0' && '5px' && '30px'
  };

  const button_newTask = {
  flex: '0' && '0' && 'auto',
  width: '100px',
  margin: 'auto' && '0',
  height: '30px',
  marginRight: '30px',
  fontFamily: 'Unica One' && 'cursive',
  fontSize: '1em'
  };

  const Components = {
  display: 'flex',
  flexFlow: 'row' && 'nowrap',
  margin: '10px' && '0',
  backgroundColor: 'white'
  };

  const componentColumn = {
  width: '32%',
  height: '700px',
  margin: 'auto',
  border: '1.5px' && 'black' && 'solid',
  backgroundColor: 'white',
  borderRadius: '10px',
  boxShadow: '10px' && '20px' && '50px' && 'grey'
  };

  const categoriesHeaders = {
  border: '1px' && 'solid' && 'black',
  borderRadius: '10px' && '10px' && '0' && '0'
  };

  const columnTitle = {
  margin: '0',
  textAlign: 'center',
  fontFamily: 'Unica One' && 'cursive',
  fontSize: '1.4em',
  borderRadius: '10px' && '10px' && '0' && '0'
  };

  const queueTitle = {
  backgroundColor: 'lightcoral'
  };

  const inProgressTitle = {
  backgroundColor: 'powderblue'
  };

  const doneTitle = {
  backgroundColor: 'mediumaquamarine'
  };

  const CardComp = {
  margin: '10px',
  border: '2px' && 'solid salmon',
  borderRadius: '3px',
  backgroundColor: 'rgb(255, 215, 210)'
  };

  const cardInfo = {
  padding: '5px' && '0' && '5px' && '20px'
  };

  const cardStatuses = {
  margin: '0'
  };
/* ---------------STYLES---------------- */



class App extends Component {
  constructor(props) {
    super(props);

    //State is an object, items is an array of objects
    this.state = {
      queueItems: [
        {
          id: 1,
          title: "Clean room",
          body: "Donate old clothes and books",
          priority: "low",
          status: "Queue",
          createdBy: "Bob",
          AssignedTo: "May"
        }
      ],
      inProgressItems: [
        {
          id: 2,
          title: "Pay bills",
          body: "Credit card bills due this month",
          priority: "high",
          status: "In Progress",
          createdBy: "Bill",
          AssignedTo: "Em"
        },
        {
          id: 4,
          title: "Go to work",
          body: "Earn money, don't be homeless",
          priority: "medium",
          status: "In Progress",
          createdBy: "Paul",
          AssignedTo: "May"
        }
      ],
      doneItems: [
        {
          id: 3,
          title: "Walk the dog",
          body: "Twice a day",
          priority: "low",
          status: "Done",
          createdBy: "Sam",
          AssignedTo: "Sammy"
        },
      ]
    }
  }

  render() {
    return (

      <div className="App">
        <style>
          @import url('https://fonts.googleapis.com/css?family=Amatic+SC:400,700|Shadows+Into+Light|Unica+One');
          </style>

        <header className={appHeader}>KANBAN</header>

        <div id={ProjectHeader}>
          <div id="projectTitle">Personal Life</div>

          <button id="newTask" type="button">+ NEW TASK</button>
        </div>

        <div className="Components">
          <Queue />
          {/* {this.state.queueItems.map(item => <div>{queueItems.title} </div>)} */}
          <InProgress />
          <Done />
        </div>

      </div>
    )
  };
}

export default App;

