import React, { Component } from 'react';
import logo from './logo.svg';
import Styles from './styles/styles.js';
import './App.css';

//Import JSX component files
import Queue from './categories/queue.jsx';
import InProgress from './categories/inProgress.jsx';
import Done from './categories/done.jsx';
import Card from './categories/card.jsx';

class App extends Component {
  constructor(props) {
    super(props);

    //State is an object, items is an array of objects
    this.state = {
      queueItems: [
        {
          id: 1,
          title: "Clean room",
          body: "Donate old clothes and books",
          priority: "low",
          status: "Queue",
          createdBy: "Bob",
          AssignedTo: "May"
        }
      ],
      inProgressItems: [
        {
          id: 2,
          title: "Pay bills",
          body: "Credit card bills due this month",
          priority: "high",
          status: "In Progress",
          createdBy: "Bill",
          AssignedTo: "Em"
        },
        {
          id: 4,
          title: "Go to work",
          body: "Earn money, don't be homeless",
          priority: "medium",
          status: "In Progress",
          createdBy: "Paul",
          AssignedTo: "May"
        }
      ],
      doneItems: [
        {
          id: 3,
          title: "Walk the dog",
          body: "Twice a day",
          priority: "low",
          status: "Done",
          createdBy: "Sam",
          AssignedTo: "Sammy"
        },
      ]
    }
  }

  render() {
    return (

      <div className="App">
        <style>
          @import url('https://fonts.googleapis.com/css?family=Amatic+SC:400,700|Shadows+Into+Light|Unica+One');
          </style>

        <header className={`Styles.appHeader`}>KANBAN</header>

        <div id={Styles().ProjectHeader}>
          <div id="projectTitle">Personal Life</div>

          <button id="newTask" type="button">+ NEW TASK</button>
        </div>

        <div className="Components">
          <Queue />
          {/* {this.state.queueItems.map(item => <div>{queueItems.title} </div>)} */}
          <InProgress />
          <Done />
        </div>

      </div>
    )
  };
}

export default App;

