import React, { Component } from 'react';

class ItemForm extends Component {
    constructor(props) {
      super(props)
      this.state ={
        title: null,
        body: null,
        priority_id: null,
        status_id: null,
        created_by: null,
        assigned_to: null
      }
    }

    handleSubmit = (e) => {
      e.preventDefault()
      console.log('SUBMITTED!!!!', this.state)
      this.props.addItemTask(this.state)
    }

    handleChange = (e) => {
      e.preventDefault()

      const { description, value } = e.target
      this.setState({
        [description] : value
      })
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>

          <label> Description:
            <input onChange={this.handleChange} type="text" name="description"/>
          </label> <br />
          <label> Priority:
            <select onChange={this.handleChange} name="priority">
              <option value="111">Low</option>
              <option value="555">Medium</option>
              <option value="999">High</option>
            </select>
          </label> <br />
          <label> Status:
            <select onChange={this.handleChange} name="priority">
              <option value="10">Queue</option>
              <option value="50">In Progress</option>
              <option value="90">Done</option>
            </select>
          </label> <br />
          <label> By:
            <input onChange={this.handleChange} type="text" name="by"/>
          </label> <br />
          <label> To:
            <input onChange={this.handleChange} type="text" name="to"/>
          </label> <br />

          <input type="submit"/>
        </form>
      )
    }

  }

export default ItemForm;
