import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import ItemForm from './components/newTask.jsx';
import QueueItemList from './components/queue.jsx';
import Top from './components/top.jsx';
import Queue from './components/queue.jsx';
import InProgress from './components/inProgress.jsx';
import Done from './components/done.jsx';

/* Syles */

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};

const progressCardStyles = {
  display: 'grid',
  marginBottom: '25px',
  padding: '10px',
  backgroundColor: '#74e0a5',
  border: '2px solid black',
  borderRadius: '10px',
  boxShadow: '5px 10px 5px #888888'
};

/* End Syles */



/* Syles */
const catWrapperDivStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr 1fr'
};

const catDivStyle = {
  display: 'grid',
  borderRight: '1px solid gray',
  borderLeft: 'none',
  borderBottom: 'none'
};
/* End Syles */

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      carditems: [],
      hasItems: true
    }
  }

  addItemTask = (getNewTask) => {
    console.log("***** AXIOS ADD NEW TASK FORM *****:", getNewTask);
    axios
    .post('/newtask', getNewTask)
        .then(newStateData => {
          console.log("\nserverData.data:", newStateData.data);
          this.setState({ carditems: newStateData.data })
        })
        .catch(err => {
          console.log("ERROR", err);
        })  
  }

  componentDidMount() {
    // getItemsFromFakeXHR()
    //   .then( items => {
    //     this.setState({ items })
    //   }, function() {
    //     console.log('this.state updated', this.state)
    //   })
  console.log('THIS IS WORKING')
    axios
    .get('/carditems')
    .then( carditems => {
      console.log("items", carditems)
      this.setState({carditems: carditems.data})
    })
    .catch( err => {
      console.log('err', err)
    })
  }



  render() {

const Section = () => (
  <div>
  <p style={pCatStyle}>IN QUEUE</p>
    <div style={{padding: '40px'}}>
        <div>
        <ItemList path="/carditems" carditems={this.state.carditems}/>
          <Router>
            <div>
                <Link className="App-title" to="/carditems">Items</Link>
              {/* <Route path="/items" component={QueueItemList.ItemList}/> */}

                <Link to="/newtask">
                  <button id="newtask" type="button">+ NEW TASK</button>
                </Link>
                <Route path="/newtask" component={() => <ItemForm addItemTask={this.addItemTask} />} />
                </div>
          </Router>
          {/* <ItemForm /> */}
        </div>
    </div>
  </div>
);

function ItemList(props) {

  return props.carditems.map( carditem => 
    <Item 
      key={carditem.card_id} 
      title={carditem.title} 
      body={carditem.body}
      priority_id={carditem.priority_id}
      status_id={carditem.status_id}
      created_by={carditem.created_by}
      assigned_to={carditem.assigned_to}
      />)
}

function Item(props) {
  console.log('props', props)

/* Do not display if status is not 'In Progress' */
if (props.status_id !== 50) {
    return null
  } else {
  return  <div style={progressCardStyles}>
            <h3 align="center">{props.title} </h3><br />
            <p>Description:<br />{props.body}</p>
            Priority: {helpers.thePriority} <br />
            Status: {helpers.theStatus} <br />
            Created by: {props.created_by} <br />
            Assigned to: {props.assigned_to} <br />
          </div>
          }
  }


    return (
      <div>
        <header>
        <Top />
        </header>
        <div style={catWrapperDivStyle}>
          <div style={catDivStyle}>
            <Queue items={this.props.items} />
            <Section />
          </div>
          <div style={catDivStyle}>
            <InProgress items={this.props.items} />
          </div>
          <div style={catDivStyle}>
            <Done items={this.props.items} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
