import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { getItemsFromFakeXHR, addItemToFakeXHR, deleteItemByIdFromFakeXHR } from './db/db';

const divTobBarStyle = {
  display: 'grid',
  gridTemplateColumns: '90% 10%'
};

const divLogoStyle = {
  backgroundColor: 'gray'
};

const divNewTaskStyle = {
  backgroundColor: 'gray',
  textAlign: 'right',
  border: '1px white'
};

const pTopStyle = {
  fontSize: '16px',
  textAlign: 'left',
  color: 'white'
};


const pStyle = {
  fontSize: '18px',
  textAlign: 'left',
  color: 'white'
};

const catWrapperDivStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr 1fr',
  border: '1px solid gray'
};

const catDivStyle = {
  display: 'grid',
  border: '1px solid gray'
};

const pCatStyle = {
  fontSize: '36px',
  textAlign: 'center',
  padding: '10px',
  color: 'gray'
};


const TopBar = () => (
  <div style={divTobBarStyle}>
      <div style={divLogoStyle}>
        <Logo />
      </div>
      <div style={divNewTaskStyle}>
        <NewTask />
      </div>
  </div>  
);

const Logo = () => (
  <div style={divLogoStyle}>
    <p style={pTopStyle}>KANBAN</p>
  </div>
);

const NewTask = () => (
  <div style={divNewTaskStyle}>
    <p style={pTopStyle}>+ New Task</p>
  </div>
);

const Section = () => (
  <div style={catWrapperDivStyle}>
    <div style={catDivStyle}><p style={pCatStyle}>In Queue</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>In Progress</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>Done</p></div>
  </div>
);

class App extends Component {
  render() {
    return (
      <div>
        <header>
        <TopBar />
        </header>
        <Section />
      </div>
    );
  }
}



export default App;
