import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { getItemsFromFakeXHR, addItemToFakeXHR, deleteItemByIdFromFakeXHR } from './db/inventory.db';
import axios from 'axios';


/* Syles */

const catDivStyle = {
  display: 'grid',
  // padding: '100px',
  // height: '900px'
  borderRight: '1px solid gray',
  borderLeft: '1px solid white',
  borderBottom: '1px solid white',
};

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};

const qeueuCardStyles = {
  display: 'grid',
  padding: '10px',
  backgroundColor: '#ffbc3f',
  border: '2px solid black',
  borderRadius: '10px',
  boxShadow: '5px 10px 5px #888888'
};

const items = {
  borderRadius: '10px'
};

/* End Syles */


class Queue extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
          // {
          //   id: 1,
          //   description: 'Make Better Styles.',
          //   priority: 'Medium',
          //   by: 'Jon',
          //   to: 'Renee'
          // },
          // {
          //   id: 2,
          //   description: 'Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles.',
          //   priority: 'Medium',
          //   by: 'Jon',
          //   to: 'Renee'
          // }
      ]
    }
  }
  renderItemList() {
    if (this.state.hasItems) {
      return <ItemList items={this.state.items}/>
    } else {
      return null
    }
  }

  render() {    
    const Section = () => (
        <div style={catDivStyle}>
        <p style={pCatStyle}>IN QUEUE</p>
        <div style={{padding: '40px'}}>
            <div style={qeueuCardStyles}>
              <Router>
                <div>
                  <Link className="App-title" to="/inventory">Inventory</Link>
                    <Link className="App-title" to="/character">Character</Link>
                    <Link className="App-title" to="/quests/123">Quests</Link>
                  <Route path="/inventory" component={ () => <ItemList items={this.state.items}/>}/>
                  <Route path="/character" component={Character}/>
                  <Route path="/quests/:id" component={Quests}/>
                </div>
              </Router>
              {this.state.items.map( item => <div style={items}>{item.description}</div> )}
              {this.state.items.map( item => <div style={items}>{item.priority}</div> )}
              {this.state.items.map( item => <div style={items}>{item.by}</div> )}
              {this.state.items.map( item => <div style={items}>{item.to}</div> )}
            </div>
        </div>
        </div>
    );


    return (
      <div>
          <Section />
      </div>
    );
  }
}



export default Queue;
