import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';

/* Syles */

const catDivStyle = {
  display: 'grid',
  // padding: '100px',
  // height: '900px'
  borderRight: '1px solid gray',
  borderLeft: '1px solid white',
  borderBottom: '1px solid white'
};

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};

const doneCardStyles = {
  display: 'grid',
  padding: '10px',
  backgroundColor: '#3fbbff',
  border: '2px solid black',
  borderRadius: '10px',
  boxShadow: '5px 10px 5px #888888'
};

const items = {
  borderRadius: '10px'
};


/* End Syles */


class Done extends Component {
  constructor(props) {
    super(props);
    this.state = {
      carditems: [],
      hasItems: true
    }
  }

addItemToInventory = (item) => {
  // addItemToFakeXHR(item)
  //   .then( items => {
  //     if (items) {
  //       this.setState({ items })
  //     }
  //   })

}

componentDidMount() {
  // getItemsFromFakeXHR()
  //   .then( items => {
  //     this.setState({ items })
  //   }, function() {
  //     console.log('this.state updated', this.state)
  //   })
console.log('THIS IS WORKING')
  axios
  .get('/carditems')
  .then( carditems => {
    console.log("items", carditems)
    this.setState({carditems: carditems.data})
  })
  .catch( err => {
    console.log('err', err)
  })
}

renderItemList() {
  if (this.state.hasItems) {
    return <ItemList carditems={this.state.carditems}/>
  } else {
    return <div><p> Error </p></div>
  }
}

  render() {
    const Section = () => (
        <div style={catDivStyle}>
        <p style={pCatStyle}>DONE</p>
        <div style={{padding: '40px'}}>
          <div style={doneCardStyles}>
              <ItemList path="/carditems" carditems={this.state.carditems}/>

          </div>
        </div>
        </div>
    );


    return (
      <div>
        <Section />
      </div>
    );
  }
}

function ItemList(props) {

  return props.carditems.map( carditem => 
    <Item 
      key={carditem.card_id} 
      title={carditem.title} 
      body={carditem.body}
      priority_id={carditem.priority_id}
      status_id={carditem.status_id}
      created_by={carditem.created_by}
      assigned_to={carditem.assigned_to}
      />)
}

function Item(props) {
  console.log('props', props)
  // function navigateTo() {

  // }
  console.log(props.title)
  return  <div>
            <h2>Title:</h2> {props.title} <br /><br />
            <h2>Description:</h2> {props.body} <br /><br />
            <h2>Priority:</h2> {props.priority_id} <br /><br />
            <h2>Status:</h2> {props.status_id} <br /><br />
            <h2>Created by:</h2> {props.created_by} <br /><br />
            <h2>Assigned to:</h2> {props.assigned_to} <br /><br />
          </div>
  }

class ItemForm extends Component {
  constructor(props) {
    super(props)
    this.state ={
      description: null,
      priority: null,
      by: null,
      to: null
    }
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log('SUBMITTED!!!!', this.state)
    this.props.addItem(this.state)
  }

  handleChange = (e) => {
    e.preventDefault()

    const { description, value } = e.target
    this.setState({
      [description] : value
    })
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>

        <label> Description:
          <input onChange={this.handleChange} type="text" name="description"/>
        </label> <br />
        <label> Priority:
          <select onChange={this.handleChange} name="priority">
            <option value="Low">Low</option>
            <option value="Medium">Medium</option>
            <option value="High">High</option>
          </select>
        </label> <br />
        <label> By:
          <input onChange={this.handleChange} type="text" name="by"/>
        </label> <br />
        <label> To:
          <input onChange={this.handleChange} type="text" name="to"/>
        </label> <br />

        <input type="submit"/>
      </form>
    )
  }

}

export default Done;
