import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Queue from './categories/queue.jsx';
import InProgress from './categories/inProgress.jsx';
import Done from './categories/done.jsx';
import Card from './categories/card.jsx';


/* Syles */
const divTobBarStyle = {
  display: 'grid',
  gridTemplateColumns: '10% 80% 10%'
};

const divLogoStyle = {
  display: 'grid',
  backgroundColor: '#343d51'
};

const divNewTaskStyle = {
  background: '#343d51',
  paddingRight: '10px',
};

const pLogoStyle = {
  fontFamily: 'Geneva',
  fontSize: '18px',
  textAlign: 'center',
  padding: '10px',
  border: '1px',
  color: 'white'
};


const pNewTaskStyle = {
  fontFamily: 'Geneva',
  fontSize: '18px',
  textAlign: 'center',
  padding: '10px',
  border: '1px solid white',
  color: 'white'
};

const catWrapperDivStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr 1fr'
};

const catDivStyle = {
  display: 'grid',
  height: '900px',
  borderRight: '1px solid gray',
  borderLeft: '1px solid white',
  borderBottom: '1px solid white',

};

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};
/* End Syles */


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
          {
            id: 1,
            description: 'Make Better Styles.',
            priority: 'Medium',
            by: 'Jon',
            to: 'Renee'
          },
          {
            id: 2,
            description: 'Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles. Make Better Styles.',
            priority: 'Medium',
            by: 'Jon',
            to: 'Renee'
          }
      ]
    }
  }

  render() {
    const TopBar = () => (
      <div style={divTobBarStyle}>
          <div style={divLogoStyle}>
            <Logo />
          </div>
          <div name="centerspace" style={{backgroundColor: '#343d51'}}>
          </div>  
          <div style={divNewTaskStyle}>
            <NewTask />
          </div>
      </div>  
    );

    const Logo = () => (
      <div style={divLogoStyle}>
        <p style={pLogoStyle}>KANBAN</p>
      </div>
    );

    const NewTask = () => (
      <div style={divNewTaskStyle}>
        <p style={pNewTaskStyle}>+ New Task</p>
      </div>
    );

    const Section = () => (
      <div style={catWrapperDivStyle}>
        <div style={catDivStyle}>
          <p style={pCatStyle}>IN QUEUE</p>
              {this.state.items.map( item => <div>{item.description}</div> )}
        </div>
        <div style={catDivStyle}>
          <p style={pCatStyle}>IN PROGRESS</p>
            <br />
              {/* <InProgress tasks={this.state.tasks} /> */}
        </div>
        <div style={catDivStyle}>
          <p style={pCatStyle}>DONE</p>
            <br />
              {/* <Done tasks={this.state.tasks} /> */}
        </div>
      </div>
    );


    return (
      <div>
        <header>
        <TopBar />
        </header>
        <Section />
      </div>
    );
  }
}



export default App;
