import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import Top from './components/top.jsx';
import Queue from './components/queue.jsx';
import InProgress from './components/inProgress.jsx';
import Done from './components/done.jsx';

/* Syles */
const catWrapperDivStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr 1fr'
};

const catDivStyle = {
  display: 'grid',
  borderRight: '1px solid gray',
  borderLeft: 'none',
  borderBottom: 'none'
};
/* End Syles */

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      carditems: [],
      hasItems: true
    }
  }

  addItemTask = (getNewTask) => {
    console.log("***** AXIOS ADD NEW TASK FORM *****:", getNewTask);
    axios
    .post('/newtask', getNewTask)
        .then(newStateData => {
          console.log("\nserverData.data:", newStateData.data);
          this.setState({ carditems: newStateData.data })
        })
        .catch(err => {
          console.log("ERROR", err);
        })  
  }

  componentDidMount() {
    // getItemsFromFakeXHR()
    //   .then( items => {
    //     this.setState({ items })
    //   }, function() {
    //     console.log('this.state updated', this.state)
    //   })
  console.log('THIS IS WORKING')
    axios
    .get('/carditems')
    .then( carditems => {
      console.log("items", carditems)
      this.setState({carditems: carditems.data})
    })
    .catch( err => {
      console.log('err', err)
    })
  }



  render() {
    return (
      <div>
        <header>
        <Top />
        </header>
        <div style={catWrapperDivStyle}>
          <div style={catDivStyle}>
            <Queue items={this.props.items} />
          </div>
          <div style={catDivStyle}>
            <InProgress items={this.props.items} />
          </div>
          <div style={catDivStyle}>
            <Done items={this.props.items} />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
