import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';
// import helpers from './helpers/helpers.jsx';
import ItemForm from './newTask.jsx';
import EditForm from './editForm.jsx';


/* Syles */

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};

const qeueuCardStyles = {
  display: 'grid',
  marginBottom: '25px',
  padding: '10px',
  backgroundColor: '#ffbc3f',
  border: '2px solid black',
  borderRadius: '10px',
  boxShadow: '5px 10px 5px #888888'
};

/* End Syles */


class Queue extends Component {
  constructor(props) {
    super(props);
    this.state = {
      carditems: [],
      usernames: [],
      hasItems: true
    }
  }

addItemTask = (getNewTask) => {
  axios
  .post('/newtask', getNewTask)
      .then(itemsData => {
        console.log("\nitemsData.data:", itemsData.data);
        this.setState({ itemsData: itemsData.data })
      })
      .catch(err => {
        console.log("ERROR", err);
      })  
}

componentDidMount() {
  // getItemsFromFakeXHR()
  //   .then( items => {
  //     this.setState({ items })
  //   }, function() {
  //     console.log('this.state updated', this.state)
  //   })
  axios
  .get('/carditems')
  .then( carditems => {
    console.log("items", carditems)
    this.setState({carditems: carditems.data})
  })
  .catch( err => {
    console.log('err', err)
  })

  axios
  .get('/usernames')
  .then( usernames => {
    console.log("items", usernames)
    this.setState({usernames: usernames.data})
  })
  .catch( err => {
    console.log('err', err)
  })

}

editTask = (taskFromEditForm, id) => {
  console.log("\n--> Editing task: ", taskFromEditForm);
  console.log("\n--> Editing task id: ", id);
  axios
    .put("/editTask", taskFromEditForm)
    .then(editServerData => {
      console.log("\nCheck - editServerData:", editServerData)
      this.setState({ carditems: editServerData.data })
    })
    .catch(err => {
      console.log("Error w/axios PUT/editTask:", err);
    })
}

deleteTask = (task) => {
  console.log("---> Deleting task", task);
}

renderItemList() {
  if (this.state.hasItems) {
    return <ItemList carditems={this.state.carditems}/>
  } else {
    return <div><p> Error </p></div>
  }
};

