render() {    
const Section = () => (
  <div>
  <p style={pCatStyle}>IN QUEUE</p>
    <div style={{padding: '40px'}}>
        <div>
          <ItemList path="/carditems" carditems={this.state.carditems}/><br />
          {/* <UserList path="/usernames" usernames={this.state.usernames}/> */}
          <Router>
            <div>
                <Link className="App-title" to="/carditems">Items</Link>
                <Link to="/editTask"><button id="Task" type="button">Edit Task</button></Link>
              <Route path="/carditems" component={ () => <ItemList items={this.state.items}/>}/>

                <Link to="/newtask">
                  <button id="newTask" type="button">+ NEW TASK</button>
                </Link>
                <Route path="/newtask" component={() => <ItemForm addItemTask={this.addItemTask} />} />
                <Route path="/editTask" component={() => <EditForm carditems={carditems} editTask={props.editTask} key={carditems.card_id} />} />
                </div>
          </Router>
          {/* <ItemForm /> */}
        </div>
    </div>
  </div>
);

return (
    <div>
      <Section />
    </div>
    );
  }
}

