import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { getItemsFromFakeXHR, addItemToFakeXHR, deleteItemByIdFromFakeXHR } from './db/db';

const divTobBarStyle = {
  display: 'grid',
  gridTemplateColumns: '10% 80% 10%'
};

const divLogoStyle = {
  display: 'grid',
  backgroundColor: '#343d51'
};

const divNewTaskStyle = {
  background: '#343d51',
  paddingRight: '10px',
};

const pLogoStyle = {
  fontSize: '18px',
  textAlign: 'center',
  color: 'white'
};


const pNewTaskStyle = {
  fontSize: '18px',
  textAlign: 'center',
  padding: '2px',
  border: '1px solid white',
  color: 'white'
};

const catWrapperDivStyle = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr 1fr',
  border: '1px solid gray'
};

const catDivStyle = {
  display: 'grid',
  height: '900px',
  borderRight: '1px solid gray',
  borderLeft: '1px solid white',
  borderBottom: '1px solid white',

};

const pCatStyle = {
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};


const TopBar = () => (
  <div style={divTobBarStyle}>
      <div style={divLogoStyle}>
        <Logo />
      </div>
      <div name="centerspace" style={{backgroundColor: '#343d51'}}>
      </div>  
      <div style={divNewTaskStyle}>
        <NewTask />
      </div>
  </div>  
);

const Logo = () => (
  <div style={divLogoStyle}>
    <p style={pLogoStyle}>KANBAN</p>
  </div>
);

const NewTask = () => (
  <div style={divNewTaskStyle}>
    <p style={pNewTaskStyle}>+ New Task</p>
  </div>
);

const Section = () => (
  <div style={catWrapperDivStyle}>
    <div style={catDivStyle}><p style={pCatStyle}>In Queue</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>In Progress</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>Done</p></div>
  </div>
);

class App extends Component {
  render() {
    return (
      <div>
        <header>
        <TopBar />
        </header>
        <Section />
      </div>
    );
  }
}



export default App;
