import React from 'react';

const headerBard = {
  display: 'inline-flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '15px',
  paddingTop: '5px',
  paddingBottom: '5px',
  textTransform: 'uppercase',
  backgroundColor: 'gray',
  width: '97%',
  borderBottom: '1px solid #E5E5E5',
}

const Header = ({ name }) => {

  return (
  <div style={headerBard}>
    <h1>{ name }</h1>
    <a href="new-task">
      <button className="add">
        + New Task
      </button>
    </a>
  </div>
  )
}

export default Header;
