import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import serviceWorker from './serviceWorker';
// 10-11-2018: per answers in https://stackoverflow.com/questions/47652627/either-wrap-the-root-component-in-a-provider-or-explicitly-pass-store-as-a
import { Provider } from 'react-redux'; //state is passed from store to every component
import { createStore, applyMiddleware, compose } from 'redux';
import thunk from 'redux-thunk';
import reducers from './reducers/'


const boundCompose = compose.bind(null, applyMiddleware(thunk));
const store = createStore(
  reducers, //pass
  window.__REDUX_DEVTOOLS_EXTENSION__ ? boundCompose(window.__REDUX_DEVTOOLS_EXTENSION__()) : boundCompose() //compose is used to group things together so it is just one arg
);

ReactDOM.render(
  <Provider store={store}>
  <App /> 
  </Provider>,
  document.getElementById('root'));
  serviceWorker();
