import React, { Component } from 'react';
import { Draggable } from 'react-beautiful-dnd';
import Queue from './queue.jsx';

let styles = {
  backgroundColor: '#627FEA'
}

class Delete extends Component {
  constructor(props) {
    super(props)
    this.state = {
      card_id: null,
      title: null,
      status: null
    }
  }

  handleDelete = (item) => {

    // NEED TO REFACTOR TO: setState()

    this.state.status_id = item.status_id
    this.state.title = item.title
    this.state.card_id = item.card_id

    this.props.sendData(this.state)

  }

  render() {

    return this.props.items
      .filter((item) => {
        return item.status === 'active'
      })
      .map((item, index) => {
        return (

          <Draggable key={item.card_id} draggableId={item.card_id} index={index} >
            {(provided, snapshot) => (
              <div ref={provided.innerRef} {...provided.draggableProps}{...provided.dragHandleProps}>
                <div style={styles} className='listItem'>
                  <Queue sendData={this.handleDelete} id={item.card_id} key={item.card_id} name={item.title} status={item.status_id} />
                  {item.body}
                </div>
              </div>
            )}
          </Draggable>

        )
      })
  }
}

export default Delete;
