import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { getItemsFromFakeXHR, addItemToFakeXHR, deleteItemByIdFromFakeXHR } from './db/db';

const divLogoStyle = {
  backgroundColor: 'gray'
};

const divNewTaskStyle = {
  backgroundColor: 'gray'
};

const pStyle = {
  fontSize: '36px',
  textAlign: 'left',
  padding: '10px',
  color: 'white'
};

const catWrapperDivStyle = {
  border: '5px solid gray'
};

const catDivStyle = {
  display: 'grid',
  border: '5px solid gray'
};

const pCatStyle = {
  fontSize: '36px',
  textAlign: 'left',
  padding: '10px',
  color: 'gray'
};


const Logo = () => (
  <div style={divLogoStyle}>
    <p style={pStyle}>  ↵   REACT FAMBAM</p>
  </div>
);

const NewTask = () => (
  <div style={divNewTaskStyle}>
    <p style={pStyle}>+ New Task</p>
  </div>
);

const Section = () => (
  <div name="wrapper">
    <div style={catDivStyle}><p style={pCatStyle}>In Queue</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>In Progress</p></div>
    <div style={catDivStyle}><p style={pCatStyle}>Done</p></div>
  </div>
);

class App extends Component {
  render() {
    return (
      <div>
        <header>
        <Logo /> <NewTask />
        </header>
        <Section />
      </div>
    );
  }
}



export default App;
