import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import axios from 'axios';

/* Syles */

const catDivStyle = {
  display: 'grid',
  // padding: '100px',
  // height: '900px'
  borderRight: '1px solid gray',
  borderLeft: '1px solid white',
  borderBottom: '1px solid white'
};

const pCatStyle = {
  fontFamily: 'Geneva',
  fontSize: '16px',
  textAlign: 'center',
  color: 'gray'
};

const progressCardStyles = {
  display: 'grid',
  padding: '10px',
  backgroundColor: '#74e0a5',
  border: '2px solid black',
  borderRadius: '10px',
  boxShadow: '5px 10px 5px #888888'
};

const items = {
  borderRadius: '10px'
};


/* End Syles */


class InProgress extends Component {
  constructor(props) {
    super(props);
    this.state = {
      carditems: [],
      hasItems: true
    }
  }

addItemToInventory = (item) => {
  // addItemToFakeXHR(item)
  //   .then( items => {
  //     if (items) {
  //       this.setState({ items })
  //     }
  //   })

}

componentDidMount() {
  // getItemsFromFakeXHR()
  //   .then( items => {
  //     this.setState({ items })
  //   }, function() {
  //     console.log('this.state updated', this.state)
  //   })
console.log('THIS IS WORKING')
  axios
  .get('/carditems')
  .then( carditems => {
    console.log("items", carditems)
    this.setState({carditems: carditems.data})
  })
  .catch( err => {
    console.log('err', err)
  })
}

renderItemList() {
  if (this.state.hasItems) {
    return <ItemList carditems={this.state.carditems}/>
  } else {
    return <div><p> Error </p></div>
  }
}

  render() {    
    const Section = () => (
        <div style={catDivStyle}>
        <p style={pCatStyle}>IN PROGRESS</p>  
        <div style={{padding: '40px'}}>
          <div style={progressCardStyles}>
          <ItemList path="/carditems" carditems={this.state.carditems}/>

          </div>
        </div>
        </div>
    );


    return (
      <div>
          <Section />
      </div>
    );
  }
}

function ItemList(props) {

  return props.carditems.map( carditem => 
    <Item 
      key={carditem.card_id} 
      title={carditem.title} 
      body={carditem.body}
      priority_id={carditem.priority_id}
      status_id={carditem.status_id}
      created_by={carditem.created_by}
      assigned_to={carditem.assigned_to}
      />)
}

function Item(props) {
  console.log('props', props)

/* Helpers */  
  function thePriority() {
    let priorityLow = "Low";
    let priorityMedium = "Medium";
    let priorityHigh = "High";
    let priorityVar = props.priority_id;

    if (priorityVar === 111) {
      return priorityLow
    } else if (priorityVar === 555) {
        return priorityMedium
      } else if (priorityVar === 999) {
          return priorityHigh
        }
  }

  function theStatus() {
    let statusQueue = "Queue";
    let statusInProgress = "In Progress";
    let statusDone = "Done";
    let statusVar = props.status_id;

    if (statusVar === 10) {
      return statusQueue
    } else if (statusVar === 50) {
        return statusInProgress
      } else if (statusVar === 90) {
          return statusDone
        }
  }

  // function userCreatedAssigned() {
  //   let userVar = props.created_by || props.assigned_to;

  //   if (userVar === props.user_id) {
  //     return props.first_name && ' ' && props.last_name
  //   } 
  // }


/* End Helpers */


/* Do not display if status is not In Progress*/
if (props.status_id !== 50) {
    return null
  } else {
  return  <div>
            <p>Title: {props.title} </p><br />
            Description: {props.body} <br />
            Priority: {thePriority()} <br />
            Status: {theStatus()} <br />
            Created by: {props.created_by} <br />
            Assigned to: {props.assigned_to} <br />
          </div>
          }
  }


export default InProgress;
