import React from 'react';

const cardComponent = {
  backgroundColor: 'lightgoldenrodyellow',
  padding: '10px',
  margin: '10px',
  boxShadow: '2px 2px 5px #755959'
};

const cardTitle = {
  textAlign: 'start'
}

const cardDetails = {
  fontSize: '12px',
  textAlign: 'start',
  padding: '10px'
}

const cardBottom = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'baseline',
  width: '100%'
}

const Card = ( {title, priority, created_by, assigned_to, handleEdit, handleDelete } ) => {
  // console.log('component card', cards)
  return(
    <div style={cardComponent}>
    <div style={cardTitle}>
      { title }
    </div>
    <div style={cardDetails}>
      Priority: { priority }
      <br />
      Assigned by: { created_by }
    </div>

    <div style={cardBottom}>
      <button className="edit-button" onChange={ handleEdit }>
        Edit
      </button>
      <button className="delete-button" onChange={ handleDelete} >
        Delete
      </button>
      <div className="assigned-to">
        { assigned_to }
      </div>
    </div>
  </div>
  )
}


export default Card;

