import * as React from 'react';
import * as PropTypes from 'prop-types';
import { IntlService } from './IntlService';
import { IntlProviderProps } from './IntlProviderProps';
/**
 * A React component which provides an internationalization service.
 * Expects a locale string as a property of the component.
 */
export default class IntlProvider extends React.Component<IntlProviderProps, {}> {
    /**
     * @hidden
     */
    static propTypes: {
        locale: PropTypes.Requireable<any>;
    };
    /**
     * @hidden
     */
    static childContextTypes: {
        kendoIntlService: PropTypes.Requireable<any>;
    };
    /**
     * Returns an internationalization service.
     * The method is suitable for overriding when you
     * implement custom internationalization behavior.
     */
    getIntlService(): IntlService;
    /**
     * @hidden
     */
    getChildContext(): {
        kendoIntlService: IntlService;
    };
    /**
     * @hidden
     */
    render(): React.ReactElement<any>;
}
